generated.css:
* {
  box-sizing: border-box;
  margin: 0;
}

body {
  min-width: 400px;
  color: #5f5f5f;
  font-family: 'Lato', sans-serif;
}

button:hover {
  cursor: pointer;
}

/*  Nav */

.navbar {
  display: flex;
  justify-content: space-between;
  padding: 15px 0;
}

.nameTitle {
  margin-left: 20px;
  font-family: 'Open Sans', sans-serif;
  font-weight: 400;
  color: #fff;
}

.iconGroup {
  display: inline-block;
  margin-right: 20px;
}

.icon {
  width: 40px;
  padding-left: 5px;
}

/* Main */

main {
  margin: 0 5%;
}

 /* Homepage */

 .mainDiv {
   width: 100%;
   background: url(85a0038ec61f461b4af735610f1f3a3f.jpeg);
   background-repeat: no-repeat;
   background-size: cover;
   background-position: center;
   min-height: 500px;
 }

 .tagline {
   font-size: 2.2rem;
   font-weight: 800;
   margin: 10px 0 10px 0;
   text-align: center;
   color: #fff;
 }

.subtagline {
  text-align: center;
  font-weight: 400;
 }

.spacer {
  width: 100%;
  height: 275px;
  background-color: transparent;
}

.buttonContainer {
  display: flex;
  justify-content: space-between;
  margin-bottom: 20px;
  width: 100%;
}

.homepageButton {
  padding: 8px 20px;
  margin: 15px;
  font-weight: 400;
  font-size: 1rem;
  color: white;
  background-color: rgba(95, 95, 95, 0.651);
  border: none;
  flex-shrink: 1;
}

/* About */

.aboutNavbar {
  display: flex;
  justify-content: space-between;
  padding: 15px 0;
  background-color: #A3C5E0;
}

.aboutContainer {
  display: flex;
  flex-wrap: wrap;
  align-content: center;
}

.aboutImg {
  width: 90%;
}

/* Media Queries */
@media all and (min-width: 650px) {

  main {
    margin: 0;
  }

  .nameTitle {
    font-size: 2rem;
  }

  .icon {
    width: 45px;
  }

  .buttonContainer {
    display: flex;
    flex-direction: column;
    width: 200px;
  }

  .spacer {
    height: 50px;
  }

  .homepageButton {
    width: 200px;
    padding-top: 10px;
    padding-bottom: 10px;
    padding-right: 40px;
    margin: 15px 0;
    text-align: right;
    font-size: 1.2rem;
  }
}

@media all and (min-width: 768px) {

  .mainDiv {
    height: 550px;
  }
}

@media all and (min-width: 992px) {
  .mainDiv {
    height: 550px;
  }
}
